script:
frappe.ui.form.on('Budget', {
	refresh(frm) {
		// your code here

    	frm.add_custom_button(__("Budget Adjustement Voucher"), function() {
            // When this button is clicked, do this

            frappe.set_route("Form", "Budget Adjustment Voucher", {"budget": frm.doc.name});

        });


    }
})

frappe.ui.form.on('Budget Adjustment Voucher', {

		// your code here
		budget: function(frm) {
        frappe.model.with_doc("Budget", frm.doc.budget, function() {
            var tabletransfer= frappe.model.get_doc("Budget", frm.doc.budget)
            $.each(tabletransfer.accounts, function(index, row){
                var d = frm.add_child("Budget Adjustment Items");
                d.account = row.account;
                d.opening_budget = row.budget_amount;
                frm.refresh_field("budget_adjustment");
            });
        });
    },

		refresh(frm) {

         }

})
